import React from 'react';
import { Switch, Route, Link } from 'react-router-dom';
import Header from './Header';
import Main from './Main';
import Login from './Login';
import Register from './Register';
import Footer from './Footer';
import EditProfilePopup from './EditProfilePopup';
import EditAvatarPopup from './EditAvatarPopup';
import AddPlacePopup from './AddPlacePopup';
import ConfirmDeletePopup from './ConfirmDeletePopup';
import ImagePopup from './ImagePopup';
import InfoTooltip from './InfoTooltip';
import api from '../utils/api';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function App() {
  const [currentUser, setCurrentUser] = React.useState({});
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [isConfirmDeletePopupOpen, setIsConfirmDeletePopupOpen] = React.useState(false);
  const [isInfoTooltipPopupOpen, setIsInfoTooltipPopupOpen] = React.useState(false);
  const [isLoading, setIsLoading] = React.useState(false);
  const [isLogged, setIsLogged] = React.useState(false);
  const [isOpenMenu, setIsOpenMenu] = React.useState(false);
  const [cards, setCards] = React.useState([]);
  const [infoTooltipContent, setInfoTooltipContent] = React.useState({});
  const [selectedCard, setSelectedCard] = React.useState({});
  const [cardToDelete, setCardToDelete] = React.useState({});  

  function getData(_id) {
    api.getProfile(_id)
      .then((data) => {
        setCurrentUser(data);
      })
      .catch((err) => console.log(err));

    // api.getInitialCards(currentUser._id)
    //   .then((data) => {
    //     setCards(data);
    //   })
    //   .catch((err) => console.log(err));
  }

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    api.changeLikeCardStatus(currentUser._id, card._id, !isLiked)
      .then((newCard) => {
          setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
      })
      .catch((err) => console.log(err));
  }

  function handleCardDelete(card) {
    setIsLoading(true);
    api.deleteCard(currentUser._id, card._id)
      .then(() => {
        setCards((state) => state.filter((c) => c._id !== card._id));
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }

  function handleUpdateUser(userData) {
    setIsLoading(true);
    api.chahgeProfile(currentUser._id, userData)
      .then((newData) => {
        setCurrentUser(newData);
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }

  function handleUpdateAvatar(avatarData, onSuccess) {
    setIsLoading(true);
    api.changeAvatar(currentUser._id, avatarData)
      .then((newData) => {
        setCurrentUser(newData);
        onSuccess();
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }

  function handleAddPlaceSubmit(cardData, onSuccess) {
    setIsLoading(true);
    api.addNewCard(currentUser._id, cardData)
      .then((card) => {
        setCards([card, ...cards]);
        onSuccess();
        closeAllPopups();
      })
      .catch((err) => console.log(err));
  }

  function handleUpdateInfoTooltip(data) {
    setInfoTooltipContent(data);
  }

  const handleEscDown = React.useCallback((evt) => {
    evt.key === 'Escape' && closeAllPopups();
  }, [])

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
    window.addEventListener('keydown', handleEscDown);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
    window.addEventListener('keydown', handleEscDown);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
    window.addEventListener('keydown', handleEscDown);
  }

  function handleCardDeleteClick(card) {
    setIsConfirmDeletePopupOpen(true);
    setCardToDelete(card);
    window.addEventListener('keydown', handleEscDown);
  }

  function handleInfoTooltipShow() {
    setIsInfoTooltipPopupOpen(true);
    window.addEventListener('keydown', handleEscDown);
  }

  function handleCardClick({link, name}) {
    setSelectedCard({
      link: link,
      name: name
    });
    window.addEventListener('keydown', handleEscDown);
  }

  function handleClickMenu() {
    setIsOpenMenu(!isOpenMenu);
  }

  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsConfirmDeletePopupOpen(false);
    setIsLoading(false);
    setIsInfoTooltipPopupOpen(false);
    window.removeEventListener('keydown', handleEscDown);
    setSelectedCard({});
    setCardToDelete({});
  }

  return (
    <div className="page__content">
      <CurrentUserContext.Provider value={currentUser, api}>

        <Switch>
          <Route path="/sign-in">
            <Header>
              <Link className="header__link" to="/sign-up">Регистрация</Link>
            </Header>
            <Login handleInfoTooltipShow={handleInfoTooltipShow} handleUpdateInfoTooltip={handleUpdateInfoTooltip} getData={getData} />
          </Route>
          <Route path="/sign-up">
            <Header>
              <Link className="header__link" to="/sign-in">Войти</Link>
            </Header>
            <Register handleInfoTooltipShow={handleInfoTooltipShow} handleUpdateInfoTooltip={handleUpdateInfoTooltip} getData={getData} />
          </Route>
          <Route path="/">
            <Header>
              <div className={`header__container ${isOpenMenu ? 'header__container_active' : ''}`}>
                <p className="header__login">123@mail.ru</p>
                <button className="header__logout-button">Выйти</button>
              </div>
              <button className={`header__menu-button ${isOpenMenu ? 'header__menu-button_active' : ''} `}onClick={handleClickMenu}></button>
            </Header>
            <Main
              cards={cards}
              onCardLike={handleCardLike}
              onCardDeleteClick={handleCardDeleteClick}
              onCardClick={handleCardClick}
              onEditProfile={handleEditProfileClick}
              onAddPlace={handleAddPlaceClick}
              onEditAvatar={handleEditAvatarClick}
            />
          </Route>

        </Switch>

        <Footer />

        <section className="popups">
          <EditProfilePopup isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} isLoading={isLoading} onUpdateUser={handleUpdateUser}/>
          <EditAvatarPopup isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} isLoading={isLoading} onUpdateAvatar={handleUpdateAvatar}/>
          <AddPlacePopup isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} isLoading={isLoading} onAddPlace={handleAddPlaceSubmit}/>
          <ConfirmDeletePopup isOpen={isConfirmDeletePopupOpen} isLoading={isLoading} card={cardToDelete} onClose={closeAllPopups} onDeleteCard={handleCardDelete}/>
          <ImagePopup card={selectedCard} onClose={closeAllPopups} />
          <InfoTooltip isOpen={isInfoTooltipPopupOpen} onClose={closeAllPopups} infoTooltipContent={infoTooltipContent} />
        </section>
      </CurrentUserContext.Provider>
  </div>
  );
}

export default App;
