class Api {
  constructor(baseUrl) {
    this._baseUrl = baseUrl;
    this._profileUrl = baseUrl + '/users/me';
    this._cardsUrl = baseUrl + '/cards';
  }

  _createRequest({url, headersObj, method, body}) {
    console.log(url, headersObj, method, body);
    return fetch(url, {method: method, headers: headersObj, body: body})
      .then((res) => {
        if (res.ok) {
          return res.json();
        }

        return Promise.reject(`Ошибка: ${res.status}`);
      })
  }

  getProfile(_id) {
    return this._createRequest({
      url: this._profileUrl,
      headersObj: {
        authorization: _id,
        'Content-Type': 'application/json'
      },
      method: 'GET'})
  }

  getInitialCards(_id) {
    return this._createRequest({
      url: this._cardsUrl,
      headersObj: {
        authorization: _id,
        'Content-Type': 'application/json'
      },
      method: 'GET'})
  }

  chahgeProfile(_id, newData) {
    return this._createRequest({
      url: this._profileUrl,
      headersObj: {
        authorization: _id,
        'Content-Type': 'application/json'
      },
      method: 'PATCH',
      body: JSON.stringify(newData)
    })
  }

  changeAvatar(_id, avatar) {
    return this._createRequest({
      url: `${this._profileUrl}/avatar`,
      headersObj: {
        authorization: _id,
        'Content-Type': 'application/json'
      },
      method: 'PATCH',
      body: JSON.stringify(avatar)
    })
  }

  addNewCard(_id, cardData) {
    return this._createRequest({
      url: this._cardsUrl,
      headersObj: {
        authorization: _id,
        'Content-Type': 'application/json'
      },
      method: 'POST',
      body: JSON.stringify(cardData)
    })
  }

  deleteCard(_id, idCard) {
    return this._createRequest({
      url: `${this._cardsUrl}/${idCard}`,
      headersObj: {
        authorization: _id,
        'Content-Type': 'application/json'
      },
      method: 'DELETE'
    })
  }

  changeLikeCardStatus(_id, idCard, like) {
    return this._createRequest({
      url: `${this._cardsUrl}/likes/${idCard}`,
      headersObj: {
        authorization: _id,
        'Content-Type': 'application/json'
      },
      method: like ? 'PUT' : 'DELETE'
    })
  }
}

const baseUrl = 'https://mesto.nomoreparties.co/v1/cohort-20';

const api = new Api(baseUrl);
export default api;
